mapper.insert:
INSERT INTO IBIMS003B (
      EMPNO						
    , EMP_NM                    
    , ATH_CD					
    , PWD						
    , LGIN_PWD					
    , APLY_STRT_DT				
    , APLY_END_DT				
    , RGST_RSN					
    , RGST_EMPNO				
    , RGST_DT					
    , DPRT_CD					
    , DPRT_NM					
    , DEL_YN					
    , HND_EMPNO					
    , HND_DETL_DTM				
	)
VALUES (
      #{empno}
    , #{empNm}
    , #{athCd}
    , #{pwd}
    , #{lginPwd}
    , #{aplyStrtDt}
    , #{aplyEndDt}
    , #{rgstRsn}
    , #{rgstEmpno}
    , #{rgstDt}
    , #{dprtCd}
    , (SELECT MAX(A.DPRT_NM)
         FROM IBIMS003B A
        WHERE A.DPRT_CD = #{dprtCd})
    , #{delYn}
    , #{hndEmpno}
    , DEFAULT
)

mapper.select:
	    /* 중복사번 체크 */
	    SELECT COUNT(EMPNO)				
  	      FROM IBIMS003B
 	     WHERE EMPNO  = #{eno}
 	       AND DEL_YN = 'N'

SELECT ATH_CD 
FROM IBIMS003B 
WHERE EMPNO = #{empno}
