  , DELT_EMPNO
  , HND_DETL_DTM
  , HND_EMPNO
  , HND_TMNL_NO
  , HND_TR_ID
  , GUID
)
VALUES

(
    #{item.menuId}
  , #{item.menuNm}
  , #{item.shtnNm}
  , #{item.hgrkMenuId}
  , SUBSTR(#{item.hgrkMenuId}, LENGTH(#{item.hgrkMenuId}), 1)
  , #{item.sortNo}
