AND R02.INSPCT_DPRT_CCD = #{inspctDprtCcd}
AND R02.END_DT IS NULL

WITH TMP AS ( WITH RECURSIVE TMP_PARAM AS ( SELECT ADD_MONTHS(TO_DATE(#{stdYrMm} || '01', 'YYYYMMDD'), -11) AS YrMm UNION ALL SELECT ADD_MONTHS(TO_DATE(YrMm, 'YYYYMMDD'), 1) FROM TMP_PARAM WHERE A < TO_DATE(#{stdYrMm} ||'01', 'YYYYMMDD') ) SELECT SUBSTR(TO_CHAR(TMP_PARAM.YrMm, 'YYYY.MM'), 3 , LENGTH(TMP_PARAM.YrMm))|| '월' AS YrMm , TO_CHAR(TMP_PARAM.YrMm, 'YYYYMM') AS YMD FROM TMP_PARAM ) SELECT A.YrMm , SUM(CASE WHEN R02.FNC_GDS_DVD_CD = '06' THEN R02.BK_AMT ELSE 0 END) / 100000000 AS lnBnd , SUM(CASE WHEN R02.FNC_GDS_DVD_CD IN ('01','02','03','04') THEN R02.BK_AMT ELSE 0 END) / 100000000 AS qtaSbtl , SUM(CASE WHEN R02.FNC_GDS_DVD_CD = '05' THEN R02.BK_AMT ELSE 0 END) / 100000000 AS invstSecur , SUM(CASE WHEN R02.FNC_GDS_DVD_CD = '07' THEN R02.BK_AMT ELSE 0 END) / 100000000 AS accdtCmnt , SUM(CASE WHEN R02.FNC_GDS_DVD_CD = '08' THEN R02.BK_AMT ELSE 0 END) / 100000000 AS accdtByng , SUM(CASE WHEN R02.FNC_GDS_DVD_CD = '09' THEN R02.BK_AMT ELSE 0 END) / 100000000 AS accdtHug FROM TMP A LEFT OUTER JOIN ( SELECT R02.IB_DEAL_NO AS IB_DEAL_NO /* DEAL번호 */ , R02.RISK_INSPCT_CCD AS RISK_INSPCT_CCD /* 리스크심사구분코드 */ , R02.LST_C_CASE_CCD AS LST_C_CASE_CCD /* 부수안건코드 */ , R02.RA_DEAL_CCD AS RA_DEAL_CCD /* RADEAL 구분코드 */ , R02.RISK_RCG_NO AS RISK_RCG_NO /* 리스크승인번호 */ , R02.IB_DEAL_NM AS IB_DEAL_NM /* DEAL명 */ , R02.PTCP_AMT AS PTCP_AMT /* 참여금액 */ , R02.RISK_INSPCT_RSLTN_CCD AS RISK_INSPCT_RSLTN_CCD /* 리스크심사결의구분코드 */ , R02.APLC_EXPT_DT AS APLC_EXPT_DT /* 적용예정일자 */ , R02.INSPCT_PRGRS_ST_CD AS INSPCT_PRGRS_ST_CD /* 심사진행상태코드 */ , R02.WRT_DT AS WRT_DT /* 기표일자 */ , R02.END_DT AS END_DT /* 종료일자 */ , R02.FNC_GDS_DVD_CD AS FNC_GDS_DVD_CD /* 금융상품분류코드 */ , R02.INSPCT_DPRT_CCD AS INSPCT_DPRT_CCD /* 심사부서구분코드 */ , R49.LN_RA_BDDT_ALW_AMT AS LN_RA_BDDT_ALW_AMT , SUBSTR(R49.STD_DT, 1, 6) AS YMD , R49.BK_AMT AS BK_AMT FROM RAA02B R02 , ( SELECT R49.STD_DT , R49.CMBS_BKNO AS CMBS_BKNO , R49.ASTS_DSTG_CHRS_NO AS ASTS_DSTG_CHRS_NO , SUM(R49.BK_AMT) AS BK_AMT , SUM(R49.LN_RA_BDDT_ALW_AMT) AS LN_RA_BDDT_ALW_AMT , MAX(R49.RISK_RCG_NO) AS RISK_RCG_NO FROM RAA49B R49 WHERE #{stdYrMm} = TO_CHAR(SYSDATE, 'YYYYMM') AND R49.STD_DT = (SELECT MAX(STD_DT) FROM RAA49B) GROUP BY R49.STD_DT , R49.ASTS_DSTG_CHRS_NO , R49.CMBS_BKNO ) R49 , ( SELECT R50.STD_DT AS STD_DT , R50.IB_DEAL_NO AS IB_DEAL_NO , R50.RISK_INSPCT_CCD AS RISK_INSPCT_CCD , R50.LST_C_CASE_CCD AS LST_C_CASE_CCD FROM RAA50B R50 WHERE R50.RPRT_XCL_F <> 'Y' ) R50 WHERE #{stdYrMm} = TO_CHAR(SYSDATE, 'YYYYMM') AND R49.STD_DT = R50.STD_DT AND R02.IB_DEAL_NO = R50.IB_DEAL_NO AND R02.RISK_INSPCT_CCD = R50.RISK_INSPCT_CCD AND R02.LST_C_CASE_CCD = R50.LST_C_CASE_CCD AND NVL(R02.RISK_RCG_NO, '999') = R49.RISK_RCG_NO AND R02.INSPCT_PRGRS_ST_CD = '500' AND R02.RA_DEAL_CCD IN ('1', '2') AND R02.FNC_GDS_DVD_CD IN ('01','02','03','04','05','06','07','08','09') AND R02.INVST_GDS_LDVD_CD NOT IN ('10', '20') AND R02.RISK_RCG_NO IS NOT NULL AND R02.END_DT IS NULL
AND R02.INSPCT_DPRT_CCD = #{inspctDprtCcd}
UNION ALL SELECT R82.IB_DEAL_NO AS IB_DEAL_NO /* DEAL번호 */ , R82.RISK_INSPCT_CCD AS RISK_INSPCT_CCD /* 리스크심사구분코드 */ , R82.LST_C_CASE_CCD AS LST_C_CASE_CCD /* 부수안건코드 */ , R82.RA_DEAL_CCD AS RA_DEAL_CCD /* RADEAL 구분코드 */ , R82.RISK_RCG_NO AS RISK_RCG_NO /* 리스크승인번호 */ , R82.IB_DEAL_NM AS IB_DEAL_NM /* DEAL명 */ , R82.PTCP_AMT AS PTCP_AMT /* 참여금액 */ , R82.RISK_INSPCT_RSLTN_CCD AS RISK_INSPCT_RSLTN_CCD /* 리스크심사결의구분코드 */ , R82.APLC_EXPT_DT AS APLC_EXPT_DT /* 적용예정일자 */ , R82.INSPCT_PRGRS_ST_CD AS INSPCT_PRGRS_ST_CD /* 심사진행상태코드 */ , R82.WRT_DT AS WRT_DT /* 기표일자 */ , R82.END_DT AS END_DT /* 종료일자 */ , R82.FNC_GDS_DVD_CD AS FNC_GDS_DVD_CD /* 금융상품분류코드 */ , R82.INSPCT_DPRT_CCD AS INSPCT_DPRT_CCD /* 심사부서구분코드 */ , R59.LN_RA_BDDT_ALW_AMT AS LN_RA_BDDT_ALW_AMT , R59.STD_YR_MM AS YMD , R59.BK_AMT AS BK_AMT FROM RAA82B R82 , ( SELECT R59.STD_YR_MM , R59.CMBS_BKNO AS CMBS_BKNO , R59.ASTS_DSTG_CHRS_NO AS ASTS_DSTG_CHRS_NO , SUM(R59.BK_AMT) AS BK_AMT , SUM(R59.LN_RA_BDDT_ALW_AMT) AS LN_RA_BDDT_ALW_AMT , MAX(R59.RISK_RCG_NO) AS RISK_RCG_NO FROM RAA59B R59 WHERE 1 = 1 GROUP BY R59.STD_YR_MM , R59.ASTS_DSTG_CHRS_NO , R59.CMBS_BKNO ) R59 , ( SELECT R50.STD_DT AS STD_DT , R50.IB_DEAL_NO AS IB_DEAL_NO , R50.RISK_INSPCT_CCD AS RISK_INSPCT_CCD , R50.LST_C_CASE_CCD AS LST_C_CASE_CCD FROM RAA50B R50 WHERE R50.RPRT_XCL_F <> 'Y' ) R50 , TMP A WHERE R82.STD_YR_MM = A.YMD AND R82.STD_YR_MM = R59.STD_YR_MM AND R82.IB_DEAL_NO = R50.IB_DEAL_NO AND R82.RISK_INSPCT_CCD = R50.RISK_INSPCT_CCD AND R82.LST_C_CASE_CCD = R50.LST_C_CASE_CCD AND LAST_DAY(TO_CHAR(R82.STD_YR_MM , 'YYYY-MM-DD')) = R50.STD_DT AND NVL(R82.RISK_RCG_NO, '999') = R59.RISK_RCG_NO AND R82.INSPCT_PRGRS_ST_CD = '500' AND R82.RA_DEAL_CCD IN ('1', '2') AND R82.FNC_GDS_DVD_CD IN ('01','02','03','04','05','06','07','08','09') AND R82.INVST_GDS_LDVD_CD NOT IN ('10', '20') AND R82.RISK_RCG_NO IS NOT NULL
AND R82.INSPCT_DPRT_CCD = #{inspctDprtCcd}
) R02 ON A.YMD = SUBSTR(R02.YMD, 1, 6) LEFT JOIN ( SELECT R91.CMNS_CD_GRP , R91.CD_VL_ID , R91.CD_VL_NM , R91.CD_SQ FROM RAA91B R91 WHERE 1=1 AND R91.CMNS_CD_GRP = 'F001' ORDER BY R91.CD_SQ ) R91 ON R91.CD_VL_ID = R02.FNC_GDS_DVD_CD WHERE 1 = 1 AND R02.END_DT IS NULL GROUP BY A.YrMm ORDER BY A.YrMm DESC
