mapper.update:
UPDATE IBIMS115B
   SET ATDC_YN       = 'Y'
     , APRV_OPPS_DCD   = #{aprvOppsDcd}
     , OPNN_RGST_DT    = DATE_FORMAT(NOW(), '%Y%m%d')
     , OPNN_RGST_EMPNO = #{opnnRgstEmpno}
     , OPNN_CTNS       = #{opnnCtns}
 WHERE CNSB_DCD = #{cnsbDcd}
   AND RSLTN_YR = #{rsltnYr}
   AND CNSB_SQ  = #{cnsbSq}
   AND SN       = #{sn}

update.foreach:
UPDATE IBIMS115B
   SET ATDC_YN       = #{item.atdcYn}
     , APRV_OPPS_DCD = #{item.aprvOppsDcd}
     , OPNN_CTNS     = #{item.opnnCtns}
     , OPNN_RGST_DT  = DATE_FORMAT(CURRENT_DATE(), '%Y%m%d')
 WHERE CNSB_DCD      = #{item.cnsbDcd}
   AND CNSB_SQ       = #{item.cnsbSq}
   AND RSLTN_YR      = #{item.rsltnYr}
   AND SN            = #{item.sn};

UPDATE IBIMS115B
  SET ATDC_YN       = ''
    , APRV_OPPS_DCD = ''
    , OPNN_CTNS     = ''
    , OPNN_RGST_DT  = ''
WHERE CNSB_DCD      = #{item.cnsbDcd}
  AND CNSB_SQ       = #{item.cnsbSq}
  AND RSLTN_YR      = #{item.rsltnYr}
  AND SN            = #{item.sn};
