mapper.delete:
DELETE
  FROM IBIMS117B
 WHERE DEAL_NO      = #{dealNo      }		
   AND JDGM_DCD     = #{jdgmDcd     }	    
   AND MTR_DCD      = #{mtrDcd      }

mapper.select:
SELECT COUNT(*)
  FROM RAA09B
 WHERE IB_DEAL_NO       = #{ibDealNo}
   AND RISK_INSPCT_CCD  = #{riskInspctCcd}
   AND LST_C_CASE_CCD   = #{lstCCaseCcd}
   AND ITEM_SQ          = #{itemSq}

mapper.update:
UPDATE RAA09B
   SET APLC_AMT            = IFNULL(#{aplcAmt}, 0)
     , END_DT_END_DT       = #{endDtEndDt}
     , HNDL_DY_TM          = SYSDATE()
     , HNDL_DPRT_CD        = #{hndlDprtCd}
     , HNDL_P_ENO 		   = #{hndlPEno}
 WHERE IB_DEAL_NO          = #{ibDealNo}
   AND RISK_INSPCT_CCD     = #{riskInspctCcd}
   AND LST_C_CASE_CCD      = #{lstCCaseCcd}
   AND ITEM_SQ             = #{itemSq}

mapper.insert:
INSERT INTO RAA09B
     (
       IB_DEAL_NO
     , RISK_INSPCT_CCD
     , LST_C_CASE_CCD
     , ITEM_SQ
     , APLC_AMT
     , END_DT_END_DT
     , HNDL_DY_TM
     , HNDL_DPRT_CD
     , HNDL_P_ENO
     )
VALUES
     (
       #{ibDealNo}
     , #{riskInspctCcd}
     , #{lstCCaseCcd}
     , (SELECT IFNULL(MAX(ITEM_SQ), 0) + 1
          FROM RAA09B
         WHERE IB_DEAL_NO        = #{ibDealNo}
             , RISK_INSPCT_CCD   = #{riskInspctCcd}
             , LST_C_CASE_CCD    = #{lstCCaseCcd}
       )
     , IFNULL(#{aplcAmt}, 0)
     , #{endDtEndDt}
     , SYSDATE()
     , HNDL_DPRT_CD
     , HNDL_P_ENO
     )
