R,0),2) * (NVL(IB402B.BUY_SHQT,0) - NVL(IB402B.SLL_SHQT,0)),2) AS EVL_PFLS_AMT , ROUND(ROUND(NVL(IB402B.AVR_UNPR,0),2) * NVL(IB402B.SLL_SHQT,0),2) AS TRAD_PFLS_AMT FROM IBIMS201B IB201B LEFT JOIN IBIMS994B IB994B ON IB201B.TR_CRRY_CD = IB994B.CRNCY_CD
