update.foreach:
UPDATE IBIMS204B
   SET  MDWY_RDMP_FEE_RTO = #{item.mdwyRdmpFeeRto}
      , HND_DETL_DTM = SYSDATE
      , HND_EMPNO = #{hndEmpno}
      , HND_TMNL_NO = #{hndTmnlNo}
      , HND_TR_ID = #{hndTrId}
      , GUID = #{guid}
 WHERE FEE_SN = #{feeSn}

mapper.delete:
DELETE FROM IBIMS204B
 WHERE 1=1
   AND PRDT_CD = #{prdtCd}
   AND FEE_SN = #{feeSn}

mapper.select:
    SELECT 
    CASE
        WHEN EXISTS (
            SELECT 1
            FROM IBIMS204B ib
            WHERE APLY_MNUM = (
                SELECT MIN(APLY_MNUM)
                FROM IBIMS204B
                WHERE APLY_MNUM >= MONTHS_BETWEEN(TO_DATE(#{stdrDt}, 'YYYYMMDD'), TO_DATE(#{endDt}, 'YYYYMMDD'))
                AND PRDT_CD = #{prdtCd}
            )
        )
        THEN (
            SELECT MDWY_RDMP_FEE_RTO
            FROM IBIMS204B ib
            WHERE APLY_MNUM = (
                SELECT MIN(APLY_MNUM)
                FROM IBIMS204B
                WHERE APLY_MNUM >= MONTHS_BETWEEN(TO_DATE(#{stdrDt}, 'YYYYMMDD'), TO_DATE(#{endDt}, 'YYYYMMDD'))
                AND PRDT_CD = #{prdtCd}
            )
            AND PRDT_CD = #{prdtCd}
        )
        ELSE (
            SELECT DISTINCT MAX(MDWY_RDMP_FEE_RTO) 
            FROM IBIMS204B
        )
    END AS MDWY_RDMP_FEE_RTO
FROM DUAL;
