mapper.select:
SELECT PRDT_CD /* 상품코드 */ , FEE_SN /* 수수료일련번호 */ , RGST_STTS_CD /* 등록상태코드 */ , PRAR_DT /* 예정일자 */ , FEE_BNAP_DCD /* 수수료선후급구분코드 */ , ( SELECT CD_VL_NM FROM IBIMS002B WHERE CMNS_CD_GRP = 'F001' AND CD_VL_ID = FEE_BNAP_DCD ) AS FEE_BNAP_NM /* 수수료선후급구분명 */ , FEE_KND_CD /* 수수료종류코드 */ , ( SELECT CD_VL_NM FROM IBIMS002B WHERE CMNS_CD_GRP = 'E008' AND CD_VL_ID = FEE_KND_CD ) AS FEE_KND_NM /* 수수료종류명 */ , IFRS_FEE_RCOG_DCD /* IFRS수수료인식구분 */ , ( SELECT CD_VL_NM FROM IBIMS002B WHERE CMNS_CD_GRP = 'F006' AND CD_VL_ID = IFRS_FEE_RCOG_DCD ) AS IFRS_FEE_RCOG_NM /* IFRS수수료인식구분명 */ , FEE_RCOG_DCD /* 수수료인식구분 */ , ( SELECT CD_VL_NM FROM IBIMS002B WHERE CMNS_CD_GRP = 'F006' AND CD_VL_ID = FEE_RCOG_DCD ) AS FEE_RCOG_DCD_NM /* 수수료인식구분명 */ , FEE_TXTN_YN /* 수수료과세여부 */ , TXTN_TP_DCD /* 과세유형구분코드 */ , ( SELECT CD_VL_NM FROM IBIMS002B WHERE CMNS_CD_GRP = 'E027' AND CD_VL_ID = TXTN_TP_DCD ) AS TXTN_TP_NM /* 과세유형구분명 */ , BUSI_NMCP_CPL_TXTN_YN /* 사업부수수료과세여부 */ , FEE_STDR_AMT /* 수수료기준금액 */ , FEE_TRGT_CTNS /* 수수료대상내용 */ , IFNULL(PRCS_CPLT_YN, '0') AS PRCS_CPLT_YN /* 처리완료여부 */ , CRRY_CD /* 통화코드 */ , FEE_RT /* 수수료율 */ , FEE_AMT /* 수수료금액 */ , FEE_RCIV_DT /* 수수료수납일자 */ , FEE_RCIV_AMT /* 수수료수납금액 */ , TR_SN /* 거래일련번호 */ , FNNR_PRLN_RTO /* 재무이연비율 */ , FNNR_RCOG_STRT_DT /* 재무인식시작일자 */ , FNNR_RCOG_END_DT /* 재무인식종료일자 */ , FNNR_PRLN_PRD_DNUM /* 재무이연기간일수 */ , MNGM_PRLN_RTO /* 관리이연비율 */ , MNGM_RCOG_STRT_DT /* 관리인식시작일자 */ , MNGM_RCOG_END_DT /* 관리인식종료일자 */ , MNGM_PRLN_PRD_DNUM /* 관리이연기간일수 */ , MNG_CNFM_YN /* 경영확인여부 */ , INOG_CNFM_YN /* 출납확인여부 */ , TAFF_CNFM_YN /* 세무확인여부 */ , PYMT_FEE /* 지급수수료 */ , RQS_RGST_YN /* 신청등록여부 */ , ACTS_CD /* 계정과목코드 */ , '' AS ACTS_NM /* 계정과목명 */ , EXC_SN /* 실행일련번호 */ , DC_RT /* 할인율 */ , RPSR_NM /* 대표자명 */ , RGST_BDCD /* 등록부점코드 */ , HND_DETL_DTM /* 조작상세일시 */ , HND_EMPNO /* 조작사원번호 */ , HND_TMNL_NO /* 조작단말기번호 */ , HND_TR_ID /* 조작거래id */ , GUID /* guid */ FROM IBIMS348H A /* 딜승인수수료스케쥴기본 */ WHERE 1 = 1 AND A.PRDT_CD = #{prdtCd} /* 'A000000001' */ AND IFNULL(A.PRCS_CPLT_YN, '0') = '0'
AND A.PRAR_DT <= #{prarDt}
AND A.RGST_STTS_CD < '2' ORDER BY FEE_SN DESC
