mapper.select:
       SELECT  
              COUNT(*) AS aplyCnt
		 FROM IBIMS404B A
		WHERE 1=1
		  AND PRDT_CD = #{prdtCd} 
		  AND ( 
		        A.APLY_STRT_DT BETWEEN #{aplyStrtDt} AND  #{aplyEndDt}
		     OR A.APLY_END_DT  BETWEEN #{aplyStrtDt} AND  #{aplyEndDt}
             OR ( A.APLY_STRT_DT <=  #{aplyStrtDt} AND  A.APLY_END_DT  >= #{aplyEndDt}))

SELECT A.APLY_STRT_DT as aplyStrtDt /* 적용시작일자 */ , A.APLY_END_DT as aplyEndDt /* 적용종료일자 */ , A.STDR_INTRT_KND_CD as stdrIntrtKndCd /* 기준금리종류코드 */ , A.FXN_INTRT as fxnIntrt /* 기준금리 */ , A.ADD_INTRT as ADD_INTRT /* 가산금리 */ , A.INTRT_CNGE_FRQC_CD as intrtCngeFrqcCd /* 적용일수구분코드 */ , A.INTRT_CNGE_FRQC_MNUM as intrtCngeFrqcMnum /* 기준금리적용일수 */ , A.APLY_DNUM_DCD as aplyDnumDcd /* 금리변동주기코드 */ , A.STDR_INTRT_APLY_DNUM as stdrIntrtAplyDnum /* 금리변동주기개월 */ , A.FXN_INTRT+A.ADD_INTRT as aplyIntrt /* 총 금리 */
FROM IBIMS404B A
WHERE A.PRDT_CD = #{prdtCd} AND A.EXC_SN = #{excSn}

SELECT A.RGST_SN AS rgstSn /* 일련번호 */ , A.APLY_STRT_DT as aplyStrtDt /* 적용시작일자 */ , A.APLY_END_DT as aplyEndDt /* 적용종료일자 */ , A.STDR_INTRT_KND_CD as stdrIntrtKndCd /* 기준금리종류코드 */ , A.FXN_INTRT as fxnIntrt /* 기준금리 */ , A.ADD_INTRT as ADD_INTRT /* 가산금리 */ , A.INTRT_CNGE_FRQC_CD as intrtCngeFrqcCd /* 적용일수구분코드 */ , A.INTRT_CNGE_FRQC_MNUM as intrtCngeFrqcMnum /* 기준금리적용일수 */ , A.APLY_DNUM_DCD as aplyDnumDcd /* 금리변동주기코드 */ , A.STDR_INTRT_APLY_DNUM as stdrIntrtAplyDnum /* 금리변동주기개월 */ , A.FXN_INTRT+A.ADD_INTRT as aplyIntrt /* 총 금리 */
FROM IBIMS404B A
WHERE A.PRDT_CD = #{prdtCd} AND A.EXC_SN = #{excSn}

mapper.delete:
		DELETE FROM IBIMS404B 
		 WHERE PRDT_CD = #{prdtCd}
           AND EXC_SN = #{excSn}
           AND RGST_SN = #{rgstSn}
