mapper.select:
		SELECT 
		       COUNT(*) AS CNT	 /* 건수 */
		  FROM IBIMS410B A 	/* 딜거래내역 */
		 WHERE 1 = 1
		   AND A.PRDT_CD  =  #{prdtCd}   
		   AND A.EXC_SN   =  #{excSn}
           AND TR_SN     >  #{trSn}
           AND TR_STAT_CD = '1'
           AND (CASE WHEN A.ETPR_CRDT_GRNT_TR_KIND_CD IN ('10','20') THEN '00' ELSE A.ETPR_CRDT_GRNT_TR_KIND_CD END) = #{etprCrdtGrntTrKindCd}

		SELECT 
		       COUNT(*) AS CNT	 /* 건수 */
		  FROM IBIMS410B A 	/* 딜거래내역 */
		 WHERE 1 = 1
		   AND A.PRDT_CD =  #{prdtCd}   
		   AND A.EXC_SN  =  #{excSn}
           AND TR_STAT_CD = '1'
		   AND (CASE WHEN A.ETPR_CRDT_GRNT_TR_KIND_CD IN ('10','20') THEN '00' ELSE A.ETPR_CRDT_GRNT_TR_KIND_CD END) = #{etprCrdtGrntTrKindCd}

SELECT 
       NVL(MAX(A.TR_SN),0)+1 AS TR_SN	 /* 거래일련번호 */
  FROM IBIMS410B A 	/* 딜거래내역 */
 WHERE 1 = 1
   AND A.PRDT_CD =  #{prdtCd} 
   AND A.EXC_SN  =  #{excSn}

mapper.update:
UPDATE IBIMS410B
   SET TR_STAT_CD  = #{trStatCd}  /* 거래상태코드 */
     , HND_DETL_DTM = SYSDATE     /* 조작상세일시 */
 WHERE 1=1
   AND PRDT_CD = #{prdtCd}
   AND EXC_SN  = #{excSn}
   AND TR_SN   = #{trSn}

