mapper.insert:
INSERT INTO IBIMS430B 
    (
        DEAL_NO 					    /*딜번호*/
        , RCTM_DT					    /*입금일자*/
        , RCTM_SEQ                      /*입금순번*/
        , RGST_SEQ					    /*등록순번*/
        , MNGM_BDCD					    /*관리부점코드*/
        , FNDS_DVSN_CD				    /*자금구분코드*/
        , DEAL_RCTM_AMT				    /*딜입금금액*/
        , PMNT_PRAR_AMT                 /*납부예정금액*/
        , DEAL_EXCS_PYMT_AMT			/*딜초과납입금액*/
        , RELT_ISTT_CD				    /*관련기관코드*/
        , RELT_ISTT_NM				    /*관련기관명*/
        , RELT_BANO					    /*관련은행계좌번호*/
        , DPTR_NM					    /*입금자명*/
        , RGST_EMPNO					/*등록사원번호*/
        , RGST_BDCD					    /*등록부서*/
        , RGST_DTM					    /*등록일시*/
    )
VALUES

    (
        #{item.dealNo}
        , #{item.rctmDt}
        , #{item.rctmSeq}
        , #{item.rgstSeq}
        , #{item.mngmBdcd}
        , #{item.fndsDvsnCd}
        , #{item.dealRctmAmt}
        , #{item.pmntPrarAmt}
        , #{item.dealExcsPymtAmt}
        , #{item.reltIsttCd}
        , #{item.reltIsttNm}
        , #{item.reltBano}
        , #{item.dptrNm}
        , #{item.rgstEmpno}
        , #{item.rgstBdcd}
        , #{item.rgstDtm}
    )

mapper.select:
SELECT A.DEAL_NO , /*딜번호*/ A.RCTM_DT , /*은행입금일자*/ A.RGST_SEQ , /*등록순번*/ A.MNGM_BDCD , /*관리부서*/ A.FNDS_DVSN_CD , /*자금구분코드*/ A.DEAL_RCTM_AMT , /*딜입금금액*/ A.PMNT_PRAR_AMT , /*납부예정금액*/ A.DEAL_EXCS_PYMT_AMT , /*딜초과납입금액*/ A.RELT_ISTT_CD , /*관련기관코드*/ A.RELT_BANO , /*관련은행계좌번호*/ A.DPTR_NM, /*입금자*/ A.RGST_BDCD , /*등록부점코드*/ ( SELECT ib.EMP_NM FROM IBIMS003B ib WHERE ib.EMPNO = A.RGST_EMPNO ) AS RGST_EMPNO , /*등록자*/ A.RGST_DTM FROM IBIMS430B A WHERE 1=1
AND DEAL_NO = #{dealNo}
AND RCTM_DT = #{rctmDt}
AND DATE_FORMAT(RGST_DTM , '%Y%m%d') = #{rgstDt}
AND RGST_SEQ = #{rgstSeq}
ORDER BY RCTM_DT, RGST_SEQ

update.foreach:
UPDATE IBIMS430B
SET 
    MNGM_BDCD = #{item.mngmBdcd},
    RDPT_OBJT_DVSN_CD = #{item.rdptObjtDvsnCd},
    EXCS_PYMT_PRCS_TEXT = #{item.excsPymtPrcsText}
WHERE
    DEAL_NO = #{item.dealNo}
AND 
    RCTM_DT = #{item.rctmDt}
AND
    RGST_SEQ = #{item.rgstSeq}

mapper.select:
SELECT DEAL_NO /*딜번호*/ , EXCS_PYMT_PRCS_TEXT /*초과납입처리내용*/ , RCTM_DT /*입금일자*/ , MNGM_BDCD /*관리부서*/ , RDPT_OBJT_DVSN_CD /*상환대상구분코드*/ , DEAL_RCTM_AMT /*딜입금금액*/ FROM IBIMS430B WHERE EXCS_PYMT_PRCS_TEXT IS NOT NULL
AND DEAL_NO = #{dealNo}
AND MNGM_BDCD = #{dprtCd}
AND RCTM_DT = #{rctmDt}

SELECT A.RCTM_DT /*입금일자*/ , B.DEAL_NO /*딜번호*/ , B.PRDT_CD /*종목코드*/ , A.MNGM_BDCD /*관리부점코드*/ , A.FNDS_DVSN_CD /*자금구분코드*/ , A.RDPT_OBJT_DVSN_CD /*상환대상구분코드*/ , A.DEAL_RCTM_AMT /*딜입금금액*/ , A.RELT_ISTT_NM /*관련기관명 == 집금은행*/ , A.RELT_BANO /*관련은행계좌번호 == 집금모계좌번호*/ , A.LNKD_BANO /*연계은행계좌번호*/ , A.LNKD_ACNT_TR_NO /*연계계좌거래번호*/ , A.EXCS_PYMT_PRCS_DVSN_CD /*초과납입처리구분코드*/ , A.DEAL_EXCS_PYMT_AMT /*딜초과납입금액*/ , A.DPTR_NM /*입금자명*/ , B.PRCS_CPLT_YN , B.PRAR_DT , ( SELECT EMP_NM FROM IBIMS003B U WHERE U.EMPNO = A.RGST_EMPNO ) AS RGST_EMPNM /*처리자*/ , A.RGST_BDCD /*처리부서*/ FROM IBIMS430B A JOIN ( SELECT CASE WHEN P.SCX_DCD = '02' THEN P.PRAR_PRNA ELSE P.RDMP_PRAR_INTR END AS PMNT_PRAR_AMT /*납부예정금액*/ , P.PRAR_DT /*상환예정일자*/ , P.PRDT_CD , D1.DEAL_NO /*딜번호*/ , D1.MNGM_BDCD /*관리부서*/ , P.SCX_DCD /*상환구분*/ , D1.TR_CRRY_CD /*통화코드*/ , P.PRCS_CPLT_YN FROM IBIMS403B P /*원리금상환스케줄*/ JOIN IBIMS201B D1 ON D1.PRDT_CD = P.PRDT_CD WHERE D1.LAST_YN = '1' AND (P.PRCS_CPLT_YN != '1' OR P.PRCS_CPLT_YN IS NULL) UNION ALL SELECT F.FEE_AMT AS PMNT_PRAR_AMT /*납부예정금액*/ , F.PRAR_DT /*상환예정일자*/ , F.PRDT_CD , D2.DEAL_NO /*딜번호*/ , D2.MNGM_BDCD /*관리부서*/ , '03' AS SCX_DCD /*상환구분*/ , D2.TR_CRRY_CD /*통화코드*/ , F.PRCS_CPLT_YN FROM IBIMS348B F /*수수료상환스케줄*/ JOIN IBIMS201B D2 ON D2.PRDT_CD = F.PRDT_CD WHERE D2.LAST_YN = '1' AND (F.PRCS_CPLT_YN != '1' OR F.PRCS_CPLT_YN IS NULL) ) AS B ON B.PRAR_DT = A.RCTM_DT AND B.SCX_DCD = A.RDPT_OBJT_DVSN_CD WHERE 1=1
AND A.RDPT_OBJT_DVSN_CD = #{rdptObjtDvsnCd}
AND B.DEAL_NO = #{dealNo}
AND A.RCTM_DT = #{rctmDt}
AND A.MNGM_BDCD = #{mngmBdcd}
