mapper.insert:
INSERT INTO IBIMS452B (
    DEAL_NO
  , EXC_SEQ
  , TR_SEQ
  , ERLM_SEQ
  , CHRR_DVSN_CD
  , RQST_STFNO
  , RELT_STFNO
  , CONS_DECD_DVSN_CD
  , CONS_DECD_STAT_CD
  , GBCK_RSON_TEXT
  , HND_DETL_DTM
  , HND_EMPNO
  , HND_TMNL_NO
  , HND_TR_ID
  , GUID
)
VALUES (
    #{dealNo}
  , #{excSeq}
  , #{trSeq}
  , (
    SELECT IFNULL(MAX(ERLM_SEQ), '0') + 1 FROM IBIMS410B
  )
  , #{chrrDvsnCd}
  , #{rqstStfno}
  , #{reltStfno}
  , #{consDecdDvsnCd}
  , #{consDecdStatCd}
  , #{gbckRsonText}
  , NOW()
  , #{hndEmpno}
  , #{hndTmnlNo}
  , #{hndTrId}
  , #{guid}
)

mapper.update:
UPDATE IBIMS452B
   SET CHRR_DVSN_CD = #{chrrDvsnCd}
     , RQST_STFNO = #{rqstStfno}
     , RELT_STFNO = #{reltStfno}
     , CONS_DECD_DVSN_CD = #{consDecdDvsnCd}
     , CONS_DECD_STAT_CD = #{consDecdStatCd}
     , GBCK_RSON_TEXT = #{gbckRsonText}
     , HND_DETL_DTM = NOW()
     , HND_EMPNO = #{hndEmpno}
     , HND_TMNL_NO = #{hndTmnlNo}
     , HND_TR_ID = #{hndTrId}
     , GUID = #{guid}
 WHERE 1=1
   AND DEAL_NO = #{dealNo}
   AND EXC_SEQ = #{excSeq}
   AND TR_SEQ = #{trSeq}
   AND ERLM_SEQ = #{erlmSeq}
