mapper.delete:
DELETE FROM IBIMS608B										
 WHERE DEAL_NO					= #{dealNo}			    	
   AND JDGM_DCD     			= #{jdgmDcd}			    
   AND MTR_DCD	    			= #{mtrDcd}			    	
   AND SQ						= #{sq}

mapper.select:
		SELECT MAX(SQ)+1 AS SQ									
  		  FROM IBIMS608B										
 		 WHERE DEAL_NO 		= #{dealNo}							
   		   AND JDGM_DCD 	= #{jdgmDcd}						
   		   AND MTR_DCD 	    = #{mtrDcd}
