mapper.select:
SELECT ROWNUM() AS rownum , A.DUDT_MNGM_NO /* 기일관리번호 */ , A.ERLM_SEQ /* 등록순번 */ , A.DEAL_NO /* 딜번호 */ , A.PRDT_CD /* 상품코드 */ , A.DUDT_DPCH_TP_DCD /* 기일통보유형코드 */ , A.CHRR_STFNO /* 담당자직원번호 */ , A.SUB_CHRR_STFNO /* 부담당자직원번호 */ , A.MNGM_ORGNO /* 관리조직번호 */ , A.ERLM_DT /* 등록일자 */ , A.ACTN_PRAR_DT /* 조치예정일자 */ , A.ACTN_DT /* 조치일자 */ , A.USE_YN /* 사용여부 */ , A.DPCH_YN /* 통보여부 */ , A.DEL_YN /* 삭제여부 */ , A.HND_DETL_DTM /* 조작상세일시 */ , A.HND_EMPNO /* 조작사원번호 */ , A.HND_TMNL_NO /* 조작단말기번호 */ , A.HND_TR_ID /* 조작거래ID */ , A.GUID /* GUID */ , B.DUDT_MNGM_DTLD_JOB_KND_CD /* 업무종류코드 */ , C.ACTS_CD /* 계정과목코드 */ , C.DEAL_NM /* 딜명 */ , D.BZEP_NAME /* 기업체 */ FROM IBIMS981B A LEFT JOIN IBIMS980B B ON A.DUDT_MNGM_NO = B.DUDT_MNGM_NO LEFT JOIN IBIMS201B C ON A.PRDT_CD = C.PRDT_CD LEFT JOIN IBIMS010B D ON C.TR_OTHR_DSCM_NO = D.ARDY_BZEP_NO WHERE 1=1 AND C.LAST_YN = 1
AND A.ACTN_PRAR_DT > #{prevDate}
AND A.ACTN_PRAR_DT < #{nextDate}
AND A.MNGM_ORGNO LIKE #{mngmOrgno} AND B.DUDT_MNGM_DTLD_JOB_KND_CD LIKE #{dudtMngmDtldJobKndCd} AND C.DEAL_NO LIKE #{dealNo} AND C.ACTS_CD LIKE #{actsCd} AND C.TR_OTHR_DSCM_NO LIKE #{ardyBzepNo}
