mapper.delete:
DELETE
  FROM IBIMS997B
 WHERE CUR_DATE = #{curDate}
   AND JOB_ID = #{jobId}

mapper.update:
UPDATE IBIMS997B
   SET JOB_STATUS = #{jobStatus} /* 2:Complete, 3:Error */
     , END_TIME = DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
     , RUN_COUNT = (SELECT (A.RUN_COUNT + 1)
                      FROM IBIMS997B A
                     WHERE A.CUR_DATE = #{curDate}
                       AND A.JOB_ID = #{jobId})
     , HND_DETL_DTM = NOW()
     , HND_EMPNO = #{hndEmpno}
     , HND_TR_ID = #{hndTrId}
 WHERE CUR_DATE = #{curDate}
   AND JOB_ID = #{jobId}

mapper.select:
        SELECT COUNT(JOB_ID)                     /* JOB ID */
          FROM IBIMS997B
         WHERE 1=1
		       AND CUR_DATE = #{curDate}
           AND JOB_ID = #{jobId}
