	 , PFRM_PLAN_CNTNT       = #{pfrmPlanCntnt     }
	 , PRCS_PLAN_CNTNT1      = #{prcsPlanCntnt1    }
	 , PRCS_PLAN_CNTNT2      = #{prcsPlanCntnt2    }
	 , VAL_SPCL_CNTNT        = #{valSpclCntnt      }
	 , APRV_DY_TM1           = #{aprvDyTm1         }
	 , APRV_P_ENO1           = #{aprvPEno1         }
	 , APRV_DY_TM2           = #{aprvDyTm2         }
