	 , FNL_MDEY_P_ENO        = #{fnlMdeyPEno       }
	 , HNDL_DY_TM            = CURRENT_TIMESTAMP()
	 , HNDL_DPRT_CD          = #{hndlDprtCd        }
	 , HNDL_P_ENO            = #{hndlPEno          }
	 , WRAP_GDS_CD           = #{wrapGdsCd         }
	 , WRAP_GDS_NM           = #{wrapGdsNm         }
	 , RPY_F                 = #{rpyF              }
	 , STD_YR_MM             = #{stdYrMm           }
	 , BSNS_OUTLN_HNGL       = #{bsnsOutlnHngl     }
	 , CNST_CMPNY_NM         = #{cnstCmpnyNm       }
	 , MAIN_PRGRS_STTN_CNTNT = #{mainPrgrsSttnCntnt}
	 , PFRM_CNTNT            = #{pfrmCntnt         }
	 , N_PFRM_RSN_CNTNT      = #{nPfrmRsnCntnt     }
 WHERE 1=1
   AND OBLG_PFRM_CCD         = #{oblgPfrmCcd       }
   AND IB_DEAL_NO            = #{ibDealNo          }
   AND RISK_INSPCT_CCD       = #{riskInspctCcd     }
   AND LST_C_CASE_CCD        = #{lstCCaseCcd       }
   AND ITEM_SQ               = #{itemSq            }

		UPDATE RAA31B														
		   SET APLC_END_DT_DT			= #{aplcEndDtDt}					
			 , OBLG_AMT					= REPLACE(#{oblgAmt}, ',', '') * 1	
			 , RPY_DT					= #{rpyDt}							
			 , RPY_AMT					= REPLACE(#{rpyAmt}, ',', '') * 1	
			 , HNDL_DY_TM				= CURRENT_TIMESTAMP()				
    	     , HNDL_DPRT_CD				= #{hndlDprtCd}						
    	     , HNDL_P_ENO				= #{hndlPEno}						 
		 WHERE IB_DEAL_NO 				= #{ibDealNo}						
		   AND RISK_INSPCT_CCD 			= #{riskInspctCcd} 					
    	   AND LST_C_CASE_CCD			= #{lstCCaseCcd}					
		   AND ITEM_SQ	  				= #{itemSq}							
		   AND OBLG_PFRM_CCD 			= '1'

mapper.delete:
DELETE FROM RAA31B
 WHERE IB_DEAL_NO 				= #{ibDealNo}				
   AND ITEM_SQ	  				= #{itemSq}					
   AND OBLG_PFRM_CCD 			= '1'

