mapper.select:
SELECT COUNT(*)
  FROM RAA36B
 WHERE OBLG_PFRM_CCD    = #{oblgPfrmCcd}
   AND IB_DEAL_NO       = #{ibDealNo}
   AND RISK_INSPCT_CCD  = #{riskInspctCcd}
   AND LST_C_CASE_CCD   = #{lstCCaseCcd}
   AND ITEM_SQ          = #{itemSq}

mapper.update:
UPDATE RAA36B
   SET RPR_PRGRS_ST_CD           = '10'
     , MAIN_PRGRS_STTN_CNTNT     = #{mainPrgrsSttnCntnt}
     , BSNS_STTN_AND_PRSPCT_SMRY = #{bsnsSttnAndPrspctSmry}
     , PRCS_PLAN_CNTNT1          = #{prcsPlanCntnt1}
     , PRCS_PLAN_CNTNT2          = #{prcsPlanCntnt2}
     , RPR_RSN_CNTNT             = #{rprRsnCntnt}
     , APRV_DY_TM1               = NULL
     , APRV_P_ENO1               = NULL
     , APRV_DY_TM2               = NULL
     , APRV_P_ENO2               = NULL
     , APRV_DY_TM3               = NULL
     , APRV_P_ENO3               = NULL
     , APRV_DY_TM4               = NULL
     , APRV_P_ENO4               = NULL
     , HNDL_DY_TM                = SYSDATE()
     , HNDL_DPRT_CD              = #{hndlDprtCd}
     , HNDL_P_ENO 		         = #{hndlPEno}
 WHERE OBLG_PFRM_CCD             = #{oblgPfrmCcd}
   AND IB_DEAL_NO                = #{ibDealNo}
   AND RISK_INSPCT_CCD           = #{riskInspctCcd}
   AND LST_C_CASE_CCD            = #{lstCCaseCcd}
   AND ITEM_SQ                   = #{itemSq}

mapper.insert:
INSERT INTO RAA36B
     (
       OBLG_PFRM_CCD
     , IB_DEAL_NO
     , RISK_INSPCT_CCD
     , LST_C_CASE_CCD
     , ITEM_SQ
     , RPR_PRGRS_ST_CD
     , MAIN_PRGRS_STTN_CNTNT
     , BSNS_STTN_AND_PRSPCT_SMRY
     , PRCS_PLAN_CNTNT1
     , PRCS_PLAN_CNTNT2
     , RPR_RSN_CNTNT
     , HNDL_DY_TM
     , HNDL_DPRT_CD
     , HNDL_P_ENO
     )
VALUES
     (
       #{oblgPfrmCcd}
     , #{ibDealNo}
     , #{riskInspctCcd}
     , #{lstCCaseCcd}
     , #{itemSq}
     , '10'
     , #{mainPrgrsSttnCntnt}
     , #{bsnsSttnAndPrspctSmry}
     , #{prcsPlanCntnt1}
     , #{prcsPlanCntnt2}
     , #{rprRsnCntnt}
     , SYSDATE()
     , #{hndlDprtCd}
     , #{hndlPEno}
     )

mapper.update:
UPDATE RAA36B
   SET RPR_PRGRS_ST_CD     = '50'
     , APRV_DY_TM2         = SYSDATE()
     , APRV_P_ENO2         = #{hndlPEno}
     , APRV_DY_TM3         = SYSDATE()
     , APRV_P_ENO3         = #{hndlPEno}
     , APRV_DY_TM4         = SYSDATE()
     , APRV_P_ENO4         = #{hndlPEno}
     , HNDL_DY_TM          = SYSDATE()
     , HNDL_DPRT_CD        = #{hndlDprtCd}
     , HNDL_P_ENO 		   = #{hndlPEno}
 WHERE IB_DEAL_NO 		   = #{ibDealNo}         /* Deal번호 */
   AND RISK_INSPCT_CCD     = #{riskInspctCcd}    /* 리스크심사구분코드 */
   AND LST_C_CASE_CCD      = #{lstCCaseCcd}     /* 부수안건구분코드 */
   AND ITEM_SQ             = #{itemSq}            /* 항목일련번호 */
   AND OBLG_PFRM_CCD 	   = #{oblgPfrmCcd}      /* 의무이행구분코드 */
